mapper.select:
SELECT p.party_no as no, p.leader_id as "leader.id", up.profile_nickname as "leader.name", pc.category_name as "category.name", p.party_name as name, p.party_cur_cnt as curCnt, p.party_quota as quota, p.party_status as status, p.updated_at as updatedAt, p.created_at as createdAt, p.party_description as description, p.party_filename as filename, p.visibility_status as visibilityStatus FROM party p JOIN user_party_application upa ON p.party_no = upa.party_no AND upa.role_no = 6 JOIN user_profile up ON upa.profile_no = up.profile_no JOIN party_category pc ON p.category_no = pc.category_no WHERE party_status = "활성"
AND p.category_no = #{catNo}
AND p.party_name like concat('%', #{value}, '%')
ORDER BY p.created_at DESC LIMIT #{beginPage}, #{rows}

mapper.update:
update party
leader_id = #{leader.id}, category_no = #{category.no}, party_name = #{name}, party_cur_cnt = #{curCnt}, party_quota = #{quota}, party_status = #{status}, visibility_status = #{visibilityStatus}, updated_at = NOW(),
party_description = #{description},
party_filename = #{filename}
where party_no = #{no}
